import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import Banner from '../../img/banner.png';
import './Worker.css'
import Profile from './Profile';
import Stats from './Stats';
import Opinion from './Opinion';
import { Pagination, Skeleton } from '@mui/material';
import Filters from './Filters';
import { getWorkerDetail,getContractUsers } from '../../redux/actions/actions';
import { useParams } from 'react-router-dom';

export const Worker = ({getWorkerDetail,getContractUsers,worker,users,isLoading}) => {

  const id = useParams().id


  useEffect(() =>{
    getWorkerDetail(id)

  },[])


  useEffect(() =>{
    if(Object.keys(worker).length !== 0){
      const contratos = []
      worker.Contracts.forEach(e => contratos.push(e.id))
      getContractUsers(contratos)
    }
    
  },[worker])


  useEffect(() =>{
    console.log(users)
  },[isLoading])

  const handleChange = (e) => {
    console.log((e.target.innerText))
  }

  return (
    <>
    {isLoading? 
    <Skeleton variant="circular">
    <div className="worker">
    <div className="w-left">
            <Profile name = {worker.name} />
        </div>
        <div>
          

        </div>
    <div className="w-portada">
        <img src={Banner} alt='banner'/>
    </div>
    <div className="w-right">
        <Stats/>
        <div className="filters">
          <Filters/>
        </div>
        <Opinion contratos={[1,2,3,4,5]}/>
        {/* siempre deberia enviarle de a 5 contratos maximo */}
        <div className="pagination">
          <Pagination count={55} onChange={handleChange} hidePrevButton hideNextButton/>
        </div>
        
        {/* la cantidad de paginas es la cantidad de contratos total dividida entre 5 */}
    </div>
  </div>
  </Skeleton>
    
    : 
    
      <div className="worker">
      <div className="w-left">
              <Profile/>
          </div>
          <div>
            

          </div>
      <div className="w-portada">
          <img src={Banner} alt='banner'/>
      </div>
      <div className="w-right">
          <Stats/>
          <div className="filters">
            <Filters/>
          </div>
          <Opinion contratos={[1,2,3,4,5]}/>
          {/* siempre deberia enviarle de a 5 contratos maximo */}
          <div className="pagination">
            <Pagination count={55} onChange={handleChange} hidePrevButton hideNextButton/>
          </div>
          
          {/* la cantidad de paginas es la cantidad de contratos total dividida entre 5 */}
      </div>
    </div>
    }

    </>
    
  )
}

const mapStateToProps = (state) => ({
  worker : state.workerDetail,
  users: state.selectedUsers,
  isLoading: state.isLoading
})

function mapDispatchToProps (dispatch) {
  return {
  getWorkerDetail : (id) => dispatch(getWorkerDetail(id)),
  getContractUsers : (ids) => dispatch(getContractUsers(ids))
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(Worker)
