export function getWorkers(query, search){


  return function (dispatch) {
    axios.get("http://localhost:3001/worker")
    .then((w) => {
      dispatch({
        type: GET_WORKERS,
      payload: w.data
    });
  })
  .catch((err) => {
    console.log(err)
  })
}

}

