}

export function orderByRating(array, orderBy){

  function order( a, b )
  {
    if ( a.rating < b.rating){
      return 1;
  }
  if ( a.rating > b.rating){
      return -1;
  }
  return 0;
  }

  if (orderBy === 'maxRating'){
    array.sort(order)
  } 
  if (orderBy === 'minRating') {
    array.sort(order).reverse()
  }
  return function (dispatch){
    dispatch ({type: ORDER_BY_RATING, payload: array})
  }
}

